mapper.insert:
INSERT INTO board(
			bd_title,
			bd_contents, 
			bd_writer,
			fixtop
) 
VALUES(
			#{bdTitle}, 
			#{bdContents}, 
			#{bdWriter}


					,#{fixtop}


					,0


)

mapper.select:
SELECT bd_no, bd_title, bd_writer, bd_count, fixtop, DATE_FORMAT(bd_cre_date, '%Y-%m-%d') as bd_cre_date, bd_contents
FROM board
WHERE bd_no = #{bdNo}

mapper.update:
UPDATE board SET
bd_title = #{bdTitle}, bd_contents = #{bdContents}, bd_cre_date = now(),
fixtop =#{fixtop}
fixtop =0
WHERE bd_no = #{bdNo}

UPDATE	board

SET		bd_count = bd_count + 1

WHERE  	bd_no = #{bdNo}
